        <menu-bar class="menu" @change-menu="changeMenuHandler"></menu-bar>
        <div class="content" v-show="showContent">
            <component :is="contentComp"></component>
        </div>
        <div class="arrow left-btn" :class="showContent ? 'left-btn-open' : ''" @click="() => showContent = !showContent"></div>
    </div>
</template>

<script>
import MenuBar from './left/MenuBar.vue';
import DefaultTemp from './left/DefaultTemp.vue';
import BgTemp from './left/BgTemp.vue';
