    components: {
        MenuBar,
        DefaultTemp,
        BgTemp
    },
    data () {
        return {
            showContent: true,
            currentMenu: {}
        };  
    },
    computed: {
        contentComp () {
            return this.currentMenu.componentName;
        }
    },
    created () {},
    mounted () {},
    methods: {
        changeMenuHandler (menuItem) {
            this.currentMenu = menuItem;
        }
    }
};
</script>

